package stepsdef;import cucumber.api.java.en.Given;import cucumber.api.java.en.Then;import cucumber.api.java.en.When;import pagesobj.ClickAllTabsPage;import pagesobj.LoginPages;public class ClickAllTabs {    ClickAllTabsPage clicktabspage = new ClickAllTabsPage();    LoginPages loginpage = new LoginPages();    @Given("^am on the \"([^\"]*)\"$")    public void amOnThe(String data ) throws Throwable {        if (data.equalsIgnoreCase("Login Page")){            loginpage.confirmTitlePage();        }if (data.equalsIgnoreCase("About US")){            clicktabspage.confirmAboutUsPage();        }else{            System.out.println("No data found for this result");        }    }    @When("^I click  the \"([^\"]*)\" Tab$")    public void iClickTheTab(String value) throws Throwable {      switch (value){          case  "TYJ":              break;          case "BYJ":              break;          case "JT":               break;          case "ABTUU":              break;              default:System.out.println("value is not valid");      }    }    @Then("^om on the \"([^\"]*)\"$")    public void omOnThe(String arg0) throws Throwable {    }}